import React, { useState, useEffect, useContext } from "react";
import Axios from 'axios';
import { withRouter, useHistory } from "react-router-dom";
import { AuthContext } from '../../context/Auth';

export const Bucket = (props) => {
    const { currentUser } = useContext(AuthContext);

    const name = props.match.params.bucketName;

    const userId = currentUser.uid;

     // to use history function
    const history = useHistory();

    document.title = `${name} | ToDo App`;

    useEffect(() => {

    }, []);



      // handle board delete
    const handleBoardDelete = (e) => {
      if (window.confirm("Are you sure you want to delete the board?")) {
        Axios
        .delete(`https://todo-app-a1032.firebaseio.com/${userId}/bucketContents/${props.location.state.bucketId}.json`)
          .then((response) => {
            alert("board deleted succesfully");
            history.push("/");
          })
          .catch((error) => console.log("Error" + error));
      }
    };



    return (
        <>

            <div className="contanier">

              <div className="row">
                <div>
                    <h2>{name}</h2>
                </div>
                <div>
                    <button className="btn btn-danger" onClick={handleBoardDelete}>Delete Bucket</button>
                </div>
              </div>

              <div className="row">
                  <div className="todo-list">
                      {/* <Card
                        pathname={props.location.pathname}
                        boardId={props.location.state.bucketId}
                        boardTitle={name}
                      ></Card> */}
                  </div>
              </div>

            </div>

        </>
      );
    }


export default withRouter(Bucket);
