import React, {useContext} from 'react';
import {NavLink , withRouter } from "react-router-dom";
import $ from "jquery";
import fire from '../../config/Fire';
import { AuthContext } from '../../context/Auth';
import './Sidebar.css';
import logo from './logo.jpg';

const Sidebar=()=> {
  const { currentUser } = useContext(AuthContext);
  const tooglesidebar=()=>
  {
    $('#sidebar').toggleClass('active');
  }

  return (

      <>
        {currentUser ? (
            <nav class="navbar navbar-expand-lg bg-orange fixed-top">
            <div class="container-fluid">
                <a href="index.php" class="navbar-brand"><img src={logo} width="50" alt="Responsive image"/><span class="logo-text">TODO</span></a>
                <button class="navbar-toggler" type="button" data-toggle="collapse"
                    data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false"
                    aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>

                <div class="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul class="navbar-nav mr-auto w-100 justify-content-end clearfix">
                        <li class="nav-item home_nav active pl-2"><NavLink class="nav-link" to="/"><i class="fa fa-book pr-2" aria-hidden="true"></i>Create Bucket</NavLink></li>
                        <li class="nav-item active pl-2"><a class="nav-link" onClick={()=>fire.auth().signOut()}><i class="fa fa-sign-out pr-2" aria-hidden="true"></i>Logout</a></li>
                    </ul>
                </div>

            </div>

      </nav>
      ) : (
      <div></div>

      )}
      </>
  );




}


export default withRouter(Sidebar);
