import React, { useState, useEffect, useContext } from "react";
import Axios from 'axios';
import { withRouter, useHistory } from "react-router-dom";
import { AuthContext } from '../../context/Auth';
import Todo from "../../components/Todo/Todo";
import './Bucket.css';

export const Bucket = (props) => {
    const { currentUser } = useContext(AuthContext);

    const name = props.match.params.bucketName;

    const userId = currentUser.uid;
    const [columnData, setColumnData] = useState({});

     // to use history function
    const history = useHistory();

    document.title = `${name} | ToDo App`;

    useEffect(() => {
      getBucketData();
    }, []);


    const getBucketData = () => {
      Axios
      .get(`https://todo-app-a1032.firebaseio.com/${userId}/bucketContents/${props.location.state.bucketId}.json`)
      .then((response) => {
          setTimeout(setColumnData(response.data), 5000);
        setLoading(false);
      })
      .catch((error) => console.log(error));
  };


      // handle board delete
    const handleBoardDelete = (e) => {
      if (window.confirm("Are you sure you want to delete the bucket?")) {
        Axios
        .delete(`https://todo-app-a1032.firebaseio.com/${userId}/bucketContents/${props.location.state.bucketId}.json`)
          .then((response) => {
            alert("board deleted succesfully");
            history.push("/");
          })
          .catch((error) => console.log("Error" + error));
      }
    };



    return (
        <>

            <div className="container bucket-section">

              <div className="row d-flex justify-content-between">
                <div>
                    <h3>{name}</h3>
                </div>
                <div>
                    <button className="btn btn-sm btn-danger" onClick={handleBoardDelete}>Delete Bucket</button>
                </div>
              </div>

              <div className="row">
                  <div className="col-lg-12 todo-list">
                      <Todo
                        pathname={props.location.pathname}
                        bucketId={props.location.state.bucketId}
                        boardTitle={name}
                      ></Todo>
                  </div>
              </div>

            </div>

        </>
      );
    }


export default withRouter(Bucket);
